// Sidebar.js
import React from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import { motion } from "framer-motion";
import {
  FiGrid, FiPackage, FiShoppingCart, FiUsers,
  FiRefreshCw, FiDollarSign, FiCpu, FiBarChart2, FiLogOut
} from "react-icons/fi";
import "./Sidebar.css";

function Sidebar() {
  const navigate = useNavigate();
  const location = useLocation();

  const handleLogout = () => {
    localStorage.clear();
    navigate("/login");
  };

  const links = [
    { to: "/", icon: <FiGrid />, label: "Dashboard" },
    { to: "/orders", icon: <FiShoppingCart />, label: "Orders" },
    { to: "/products", icon: <FiPackage />, label: "Products" },
    { to: "/customers", icon: <FiUsers />, label: "Customers" },
    { to: "/inventory", icon: <FiRefreshCw />, label: "Inventory" },
    { to: "/returns", icon: <FiRefreshCw />, label: "Returns" },
    { to: "/payments", icon: <FiDollarSign />, label: "Payment" },
    { to: "/integration", icon: <FiCpu />, label: "Integration" },
    { to: "/reports", icon: <FiBarChart2 />, label: "Reports" },
  ];

  return (
    <motion.div
      className="sidebar"
      initial={{ x: -80, opacity: 0 }}
      animate={{ x: 0, opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <div className="sidebar-header">
        ðŸ§  <span>OMS</span>
      </div>
      <ul className="sidebar-links">
        {links.map((link, index) => (
          <li key={index} className={location.pathname === link.to ? "active" : ""}>
            <Link to={link.to}>
              {link.icon}
              <span>{link.label}</span>
            </Link>
          </li>
        ))}
        <li>
          <button onClick={handleLogout} className="logout-btn">
            <FiLogOut />
            <span>Logout</span>
          </button>
        </li>
      </ul>
    </motion.div>
  );
}

export default Sidebar;
