import React from "react";
import "./Inventory.css";

// Sample product data with sales speed
const inventoryData = [
  { id: "P001", name: "Smartphone", stock: 15, salesPerDay: 50 },
  { id: "P002", name: "Laptop", stock: 5, salesPerDay: 60 },
  { id: "P003", name: "Wireless Earbuds", stock: 2, salesPerDay: 25 },
  { id: "P004", name: "Smart Watch", stock: 18, salesPerDay: 10 },
  { id: "P005", name: "Bluetooth Speaker", stock: 0, salesPerDay: 40 },
  { id: "P006", name: "Tablet", stock: 9, salesPerDay: 5 }, // should not alert
];

const STOCK_THRESHOLD = 10;
const SALES_THRESHOLD = 30;

function Inventory() {
  const isCritical = (item) => {
    return (
      item.stock === 0 ||
      (item.stock < STOCK_THRESHOLD && item.salesPerDay > SALES_THRESHOLD)
    );
  };

  return (
    <div className="page">
      <h2>Inventory</h2>
      <table className="inventory-table">
        <thead>
          <tr>
            <th>Product ID</th>
            <th>Product</th>
            <th>Stock</th>
            <th>Sales/Day</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {inventoryData.map((item) => {
            const alert = isCritical(item);
            return (
              <tr key={item.id}>
                <td>{item.id}</td>
                <td>{item.name}</td>
                <td>{item.stock}</td>
                <td>{item.salesPerDay}</td>
                <td>
                  {alert ? (
                    <span className="low-stock">⚠️ Critical</span>
                  ) : (
                    <span className="in-stock">✔️ Normal</span>
                  )}
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export default Inventory;
