import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Login.css";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = () => {
    const users = [
      { email: "admin@example.com", password: "admin123", role: "admin" },
      { email: "viewer@example.com", password: "viewer123", role: "viewer" },
      { email: "warehouse@example.com", password: "warehouse123", role: "warehouse" },
    ];

    const user = users.find((u) => u.email === email && u.password === password);

    if (user) {
      localStorage.setItem("loggedIn", "true");
      localStorage.setItem("userRole", user.role);
      navigate("/welcome"); // show intro
    } else {
      alert("Invalid credentials.");
    }
  };

  return (
    <div className="login-container">
      {/* â›” No form tag = no password save prompt */}
      <div className="login-form">
        <h2>Login</h2>
        <input
          type="text"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          autoComplete="off"
        />
        <input
          type="text" // ðŸ‘ˆ NOT password type
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          autoComplete="new-password"
        />
        <button onClick={handleLogin}>Login</button>
      </div>
    </div>
  );
};

export default Login;
