// src/pages/AddPayment.js
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./AddPayment.css"; // Optional: create this file for styling

function AddPayment() {
  const [payment, setPayment] = useState({
    orderId: "",
    amount: "",
    method: "UPI",
    status: "Pending",
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setPayment({ ...payment, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // For now, just log it. Later you can POST to backend using axios
    console.log("✅ Payment submitted:", payment);
    alert("✅ Payment submitted successfully!");

    // Redirect back to payments list after submission
    navigate("/payments");
  };

  return (
    <div className="page add-payment-page">
      <h2>Add Payment</h2>
      <form onSubmit={handleSubmit} className="form">
        <label>
          Order ID:
          <input
            type="text"
            name="orderId"
            value={payment.orderId}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Amount (₹):
          <input
            type="number"
            name="amount"
            value={payment.amount}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Method:
          <select name="method" value={payment.method} onChange={handleChange}>
            <option value="UPI">UPI</option>
            <option value="Cash">Cash</option>
            <option value="Card">Card</option>
          </select>
        </label>

        <label>
          Status:
          <select name="status" value={payment.status} onChange={handleChange}>
            <option value="Pending">Pending</option>
            <option value="Paid">Paid</option>
            <option value="Failed">Failed</option>
          </select>
        </label>

        <button type="submit">Submit Payment</button>
      </form>
    </div>
  );
}

export default AddPayment;
