// Sidebar.js
import React from "react";
import { NavLink, useNavigate } from "react-router-dom";
import { FiGrid, FiShoppingCart, FiPackage, FiUsers, FiRefreshCw, FiDollarSign, FiCpu, FiBarChart2, FiLogOut } from "react-icons/fi";
import "./Sidebar.css";

const links = [
  { to: "/", icon: <FiGrid />, label: "Dashboard" },
  { to: "/orders", icon: <FiShoppingCart />, label: "Orders" },
  { to: "/products", icon: <FiPackage />, label: "Products" },
  { to: "/customers", icon: <FiUsers />, label: "Customers" },
  { to: "/inventory", icon: <FiRefreshCw />, label: "Inventory" },
  { to: "/returns", icon: <FiRefreshCw />, label: "Returns" },
  { to: "/payments", icon: <FiDollarSign />, label: "Payment" },
  { to: "/integration", icon: <FiCpu />, label: "Integration" },
  { to: "/reports", icon: <FiBarChart2 />, label: "Reports" },
];

function Sidebar() {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.clear();
    navigate("/login");
  };

  return (
    <div className="sidebar">
      <h2 className="logo">ðŸ’  OMS</h2>
      <nav className="nav-links">
        {links.map((link, index) => (
          <NavLink to={link.to} key={index} className={({ isActive }) => (isActive ? "active" : "")}>
            <span className="icon">{link.icon}</span>
            <span className="label">{link.label}</span>
          </NavLink>
        ))}
      </nav>
      <button className="logout-button" onClick={handleLogout}>
        <FiLogOut /> Logout
      </button>
    </div>
  );
}

export default Sidebar;
