import React, { useState } from "react";
import api from "../api/api";
import "./NewOrder.css";

const customers = ["Amit Patel", "Priya Sharma", "Rahul Verma", "Neha Desai"];

const products = [
  { name: "Smartphone", price: 15000 },
  { name: "Laptop", price: 50000 },
  { name: "Wireless Earbuds", price: 2500 },
  { name: "Smart Watch", price: 4500 },
];

function NewOrder() {
  const [formData, setFormData] = useState({
    customer: "",
    product: "",
    quantity: 1,
    status: "Pending", // hidden by default
  });

  const [productPrice, setProductPrice] = useState(0);
  const [total, setTotal] = useState(0);

  const handleChange = (e) => {
    const { name, value } = e.target;

    setFormData((prevData) => {
      const newData = { ...prevData, [name]: value };

      // Auto-calculate total if product or quantity changes
      const selected = products.find((p) => p.name === newData.product);
      const price = selected ? selected.price : 0;
      setProductPrice(price);

      const qty = parseInt(newData.quantity || 1);
      setTotal(price * qty);

      return newData;
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Order Submitted:", {
      ...formData,
      price: productPrice,
      total: total,
    });
    alert("Order submitted (check console)");
  };

  return (
    <div className="page">
      <h2>Create New Order</h2>
      <form className="order-form" onSubmit={handleSubmit}>
        <label>
          Customer:
          <select name="customer" value={formData.customer} onChange={handleChange} required>
            <option value="">-- Select Customer --</option>
            {customers.map((cust) => (
              <option key={cust} value={cust}>{cust}</option>
            ))}
          </select>
        </label>

        <label>
          Product:
          <select name="product" value={formData.product} onChange={handleChange} required>
            <option value="">-- Select Product --</option>
            {products.map((prod) => (
              <option key={prod.name} value={prod.name}>{prod.name}</option>
            ))}
          </select>
        </label>

        <label>
          Quantity:
          <input
            type="number"
            name="quantity"
            value={formData.quantity}
            min="1"
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Price (per unit):
          <input type="text" value={`₹${productPrice}`} disabled />
        </label>

        <label>
          Total Amount:
          <input type="text" value={`₹${total}`} disabled />
        </label>

        <button type="submit">Submit Order</button>
      </form>
    </div>
  );
}

export default NewOrder;
