import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Integration.css";

function Integration() {
  const [integrations, setIntegrations] = useState([]);

  useEffect(() => {
    axios.get("/api/integrations").then((res) => setIntegrations(res.data));
  }, []);

  const toggleConnection = async (name) => {
    const res = await axios.put(`/api/integrations/toggle/${name}`);
    setIntegrations((prev) =>
      prev.map((i) => (i.name === name ? res.data : i))
    );
  };

  return (
    <div className="integration-page">
      <h2>ðŸ”— Integration Center</h2>
      <table className="integration-table">
        <thead>
          <tr>
            <th>System</th>
            <th>Description</th>
            <th>Status</th>
            <th>Toggle</th>
          </tr>
        </thead>
        <tbody>
          {integrations.map((i) => (
            <tr key={i.name}>
              <td>{i.name}</td>
              <td>{i.description}</td>
              <td>{i.connected ? "ðŸŸ¢ Connected" : "ðŸ”´ Disconnected"}</td>
              <td>
                <button onClick={() => toggleConnection(i.name)}>
                  {i.connected ? "Disconnect" : "Connect"}
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Integration;
