import React, { useState } from "react";
import api from "../api/api";
import "./NewOrder.css";

function NewOrder() {
  const [formData, setFormData] = useState({
    customer: "",
    product: "",
    quantity: 1,
    status: "Pending",
  });

  const [productPrice, setProductPrice] = useState(0);
  const [total, setTotal] = useState(0);

  const handleChange = (e) => {
    const { name, value } = e.target;

    setFormData((prevData) => {
      const newData = { ...prevData, [name]: value };

      // If product name matches known product, set price
      const prices = {
        "Smartphone": 15000,
        "Laptop": 50000,
        "Wireless Earbuds": 2500,
        "Smart Watch": 4500,
      };

      const price = prices[newData.product] || 0;
      setProductPrice(price);

      const qty = parseInt(newData.quantity || 1);
      setTotal(price * qty);

      return newData;
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const order = {
      ...formData,
      price: productPrice,
      total: total,
      date: new Date().toISOString(),
    };

    try {
      // Uncomment below line if backend is ready
      await api.post("/orders", order);
      console.log("Order Submitted:", order);
      alert("✅ Order submitted successfully!");
    } catch (error) {
      console.error("❌ Failed to submit order:", error);
      alert("Something went wrong.");
    }
  };

  return (
    <div className="page">
      <h2>Create New Order</h2>
      <form className="order-form" onSubmit={handleSubmit}>
        <label>
          Customer Name:
          <input
            type="text"
            name="customer"
            value={formData.customer}
            onChange={handleChange}
            placeholder="Enter customer name"
            required
          />
        </label>

        <label>
          Product Name:
          <input
            type="text"
            name="product"
            value={formData.product}
            onChange={handleChange}
            placeholder="Enter product name"
            required
          />
        </label>

        <label>
          Quantity:
          <input
            type="number"
            name="quantity"
            value={formData.quantity}
            min="1"
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Price (per unit):
          <input type="text" value={`₹${productPrice}`} disabled />
        </label>

        <label>
          Total Amount:
          <input type="text" value={`₹${total}`} disabled />
        </label>

        <button type="submit">Submit Order</button>
      </form>
    </div>
  );
}

export default NewOrder;
