import React, { useState } from "react";

function AddPayment() {
  const [payment, setPayment] = useState({
    orderId: "",
    amount: "",
    method: "UPI",
    status: "Pending",
  });

  const handleChange = (e) => {
    setPayment({ ...payment, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("Payment submitted:", payment);
    alert("✅ Payment submitted (mock)");
    // You can connect this to backend later using axios or api.js
  };

  return (
    <div className="page">
      <h2>Add Payment</h2>
      <form onSubmit={handleSubmit} className="form">
        <label>
          Order ID:
          <input type="text" name="orderId" value={payment.orderId} onChange={handleChange} />
        </label>
        <label>
          Amount (₹):
          <input type="number" name="amount" value={payment.amount} onChange={handleChange} />
        </label>
        <label>
          Method:
          <select name="method" value={payment.method} onChange={handleChange}>
            <option value="UPI">UPI</option>
            <option value="Cash">Cash</option>
            <option value="Card">Card</option>
          </select>
        </label>
        <label>
          Status:
          <select name="status" value={payment.status} onChange={handleChange}>
            <option value="Pending">Pending</option>
            <option value="Paid">Paid</option>
            <option value="Failed">Failed</option>
          </select>
        </label>
        <button type="submit">Submit Payment</button>
      </form>
    </div>
  );
}

export default AddPayment;
