// src/pages/Login.js
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Login.css";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();

    // Dummy users
    const users = [
      { email: "admin@example.com", password: "admin123", role: "admin" },
      { email: "viewer@example.com", password: "viewer123", role: "viewer" },
      { email: "warehouse@example.com", password: "warehouse123", role: "warehouse" },
    ];

    const user = users.find(
      (u) => u.email === email && u.password === password
    );

    if (user) {
      localStorage.setItem("loggedIn", "true");
      localStorage.setItem("userRole", user.role);
      navigate("/welcome"); // ðŸš€ Redirect to intro page
    } else {
      alert("Invalid credentials."); // Only for wrong login
    }
  };

  return (
    <div className="login-container">
     <form onSubmit={handleLogin} className="login-form" autoComplete="off">
       <h2>Login</h2>

       {/* ðŸ‘» Hidden fake fields to prevent browser autocomplete */}
       <input type="text" name="fakeuser" autoComplete="username" style={{ display: "none" }} />
       <input type="password" name="fakepass" autoComplete="new-password" style={{ display: "none" }} />

       <input
         type="email"
         placeholder="Email"
         value={email}
         onChange={(e) => setEmail(e.target.value)}
         required
         autoComplete="off"
       />
       <input
         type="password"
         placeholder="Password"
         value={password}
         onChange={(e) => setPassword(e.target.value)}
         required
         autoComplete="new-password"
       />
       <button type="submit">Login</button>
     </form>
    </div>
  );
};

export default Login;
