import React, { useEffect, useState } from "react";
import api from "../api/api";
import "./Inventory.css";

const STOCK_THRESHOLD = 10;
const SALES_THRESHOLD = 30;

function Inventory() {
  const [inventoryData, setInventoryData] = useState([]);

  // ✅ Fetch products from backend on page load
  useEffect(() => {
    async function fetchInventory() {
      try {
        const res = await api.get("/products");
        setInventoryData(res.data);
      } catch (err) {
        console.error("❌ Error fetching inventory:", err);
        alert("Failed to load inventory.");
      }
    }

    fetchInventory();
  }, []);

  // ✅ Check if stock is critical
  const isCritical = (item) => {
    return (
      item.stock === 0 ||
      (item.stock < STOCK_THRESHOLD && item.salesPerDay > SALES_THRESHOLD)
    );
  };

  // ✅ Restock item using backend API
  const restockItem = async (id, name, currentStock) => {
    const confirm = window.confirm(
      `⚠️ Are you sure you physically have more stock of "${name}" in warehouse?`
    );
    if (!confirm) return;

    try {
      const newStock = currentStock + 20;

      // ✅ Fixed: send stock update as an object
      await api.put(`/products/${id}/stock`, { stock: newStock });

      const updated = inventoryData.map((item) =>
        item._id === id ? { ...item, stock: newStock } : item
      );
      setInventoryData(updated);
      alert(`✅ Stock updated for ${name}`);
    } catch (err) {
      console.error("❌ Failed to update stock:", err);
      alert("Failed to update stock.");
    }
  };

  return (
    <div className="page">
      <h2>Inventory</h2>
      <table className="inventory-table">
        <thead>
          <tr>
            <th>Product</th>
            <th>Stock</th>
            <th>Sales/Day</th>
            <th>Status</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {inventoryData.map((item) => {
            const alert = isCritical(item);
            return (
              <tr key={item._id}>
                <td>{item.name}</td>
                <td>{item.stock}</td>
                <td>{item.salesPerDay || 0}</td>
                <td>
                  {alert ? (
                    <span className="low-stock">⚠️ Critical</span>
                  ) : (
                    <span className="in-stock">✔️ Normal</span>
                  )}
                </td>
                <td>
                  {alert && (
                    <button
                      className="restock-btn"
                      onClick={() => restockItem(item._id, item.name, item.stock)}
                    >
                      ➕ Restock
                    </button>
                  )}
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export default Inventory;
